function getSvgIcon(name) {
    name = name.toLowerCase();
    name = name.replace(
        / /gi,
        ""
    ); /* результат - строка в нижнем регистре,без пробелов */
    return <Fragment>{icons[name]}</Fragment>;
}

export default getSvgIcon;
